import React from 'react';

// import '../css/List.css'

export default class List extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            checkedIndex: [],
        };

        this.handleCheckChange = this.handleCheckChange.bind(this);
    }

    handleCheckChange(index, event) {
        if(event.target.checked){
            this.setState({
                checkedIndex: this.state.checkedIndex.concat(index),
            });
        }else{
            this.setState({
                checkedIndex: this.state.checkedIndex.filter((el) => {
                    return el !== index;
                }),
            });
        }

    }

    render() {
        const listItems = this.props.todoList.map((todo, index) => {
            return (
                <li key={index}>
                    <input
                        type="checkbox"
                        checked={this.state.check}
                        onChange={(event) => this.handleCheckChange(index, event)} />
                    {todo}
                </li>
            );
        });

        return (
            <ul>
                {listItems}
            </ul>
        );
    }
}
