import React from 'react';

// import '../css/Form.css'

class Form extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            todoText: '',
        };
    }

    render() {
        return (
            <div className="form">
                <form onSubmit={(event) => this.props.addTodo('aaa', event)}>
                    <p><input type="text" /></p>
                    <p><button type="submit">追加する</button></p>
                </form>
            </div>
        );
    }
}

export default Form;
