import React from 'react';

// import '../css/List.css'

export default class List extends React.Component{
    constructor(props) {
        super(props);

        this.handleCheckChange = this.handleCheckChange.bind(this);
    }

    handleCheckChange(changedItemIndex, event) {
        if(event.target.checked){
            this.setState({
                checkedIndex: this.state.checkedIndex.concat(changedItemIndex),
            });
        }else{
            this.setState({
                checkedIndex: this.state.checkedIndex.filter((el) => {
                    return el !== changedItemIndex;
                }),
            });
        }
    }

    render() {
        const listItems = this.props.todoList.map((todoObj, index) => {
            return (
                <li key={index}>
                    <input
                        type="checkbox"
                        checked={this.props.todoList[index].isChecked}
                        onChange={(event) => this.handleCheckChange(index, event)} />
                    {todo}
                </li>
            );
        });

        return (
            <div className="list">
                <ul>
                    {listItems}
                </ul>
                <button
                    type="button"
                    onClick={(event) => this.props.deleteTodo(this.state.checkedIndex, event)}>削除する</button>
            </div>
        );
    }
}
